INSERT INTO `categories` (`id`, `name`, `slug`, `image`, `parent_id`, `description`, `position`, `is_active`, `type`, `created_at`, `updated_at`) VALUES
(86, 'Combo du lịch', 'combo-du-lich', 'upload/category/du-lich-ha-khau.jpg', 0, '<p>Vietcenter Tourist cung cấp các gói combo du lịch 2020, tour, khách sạn với chi phí rất hợp lý cho kỳ nghỉ của bạn. Các điểm đến nổi bật của chúng tôi là Combo Sapa, Combo Đà Nẵng, Combo Đà Lạt, Combo Phú Quốc.</p>', 1, 1, 1, '2021-03-31 05:35:19', '2021-04-27 07:11:16'),
(87, 'Tour trong nước', 'tour-trong-nuoc', 'upload/category/du-lich-dao-ngoc-cat-ba-vietcenter.png', 0, '<p>Du lịch trong nước luôn là lựa chọn tuyệt vời. Đường bờ biển dài hơn 3260km, những khu bảo tồn thiên nhiên tuyệt vời, những thành phố nhộn nhịp, những di tích lịch sử hào hùng, nền văn hóa độc đáo và hấp dẫn, cùng một danh sách dài những món ăn ngon nhất thế giới, Việt Nam có tất cả những điều đó. Với lịch trình dày, khởi hành đúng thời gian cam kết, Vietcenter là công ty lữ hành uy tín nhất hiện nay tại Việt Nam, luôn sẵn sàng phục vụ du khách mọi lúc, mọi nơi, đảm bảo tính chuyên nghiệp và chất lượng dịch vụ tốt nhất thị trường.</p>', 2, 1, 1, '2021-03-31 05:35:57', '2021-04-27 07:11:38'),
(88, 'Tour nước ngoài', 'tour-nuoc-ngoai', 'upload/category/tour-ha-long-cat-ba.jpg', 0, '<p>Du lịch Nước Ngoài: Với những điểm đến là các Quốc gia nổi tiếng về du lịch, những thắng cảnh đẹp và cổ kính hay hiện đại. Những thành phố du lịch nguy nga tráng lệ. Tất cả sẽ có trong các hành trình du lịch nước ngoài của Vietcenter. Hãy cùng khám phá thế giới cùng Vietcenter nhé.</p>', 2, 1, 1, '2021-03-31 05:36:13', '2021-04-27 07:12:02'),
(89, 'Tour theo chủ đề', 'tour-theo-chu-de', 'upload/category/Sam-son-min.jpg', 0, '<p>Với hàng ngàn đường tour hấp dẫn khắp Việt Nam và trên quốc tế, Viecenter đã phân loại du lịch theo các chủ đề hấp dẫn mà nhiều khách du lịch yêu thích và quan tâm. Tại đây, du khách sẽ tìm kiếm dễ dàng các loại hình phù hợp với sở thích, tính cách, mong ước và kế hoạch riêng mình, như du lịch biển, trăng mật, du lịch chậm cho người cao tuổi, hành hương, lặn biển, khám phá...<p>', 3, 1, 1, '2021-03-31 05:36:27', '2021-04-27 07:12:27'),
(90, 'Lịch khởi hành', 'lich-khoi-hanh', 'upload/category/ve-may-bay-da-lat.png', 0, NULL, 1, 0, 3, '2021-03-31 05:36:51', '2021-04-24 10:46:54'),
(91, 'Tin tức', 'tin-tuc', 'upload/category/sai-gon.jpg', 0, NULL, 1, 1, 3, '2021-03-31 05:37:03', '2021-04-06 05:13:32'),
(92, 'Combo Sapa', 'combo-sapa', 'upload/category/nha-tho-da-co-sapa.jpg', 86, '<p>Combo du lịch sapa 2020 - Vietcenter Tourist cung cấp c&aacute;c g&oacute;i Combo du lịch tại Sapa với gi&aacute; tốt nhất. C&oacute; c&aacute;c combo Sapa từ kh&aacute;ch sạn 2 sao đến kh&aacute;ch sạn 5 sao. Đa dạng lựa chọn thời gian th&iacute;ch hợp tại C&aacute;c g&oacute;i Combo Sapa 3 ng&agrave;y 2 đ&ecirc;m. Combo Sapa 2 ng&agrave;y 1 đ&ecirc;m. Li&ecirc;n hệ 0968.178.011 để được hỗ trợ tư vấn v&agrave; đặt combo du lịch Sapa uy t&iacute;n v&agrave; chất lượng nhất.<p>', 2, 1, 1, '2021-03-31 05:37:20', '2021-04-27 07:12:41'),
(93, 'Combo Phú Quốc', 'combo-phu-quoc', 'upload/category/du-lich-phu-quoc-vietcenter.jpg', 86, '<p>Combo du lịch Ph&uacute; Quốc 2020 - Vietcenter Tourist cung cấp c&aacute;c g&oacute;i Combo du lịch Ph&uacute; Quốc với gi&aacute; tốt nhất. C&oacute; c&aacute;c Combo Ph&uacute; Quốc từ kh&aacute;ch sạn 3 sao đến kh&aacute;ch sạn 5 sao. Đa dạng lựa chọn thời gian th&iacute;ch hợp tại C&aacute;c g&oacute;i Combo Ph&uacute; Quốc 3 ng&agrave;y 2 đ&ecirc;m. Combo Ph&uacute; Quốc 4 ng&agrave;y 3 đ&ecirc;m. Li&ecirc;n hệ 0968.178.011 để được hỗ trợ tư vấn v&agrave; đặt Combo du lịch Ph&uacute; Quốc uy t&iacute;n v&agrave; chất lượng nhất.</p>', 2, 1, 1, '2021-03-31 05:37:34', '2021-04-27 07:12:55'),
(94, 'Combo Đà Nẵng', 'combo-da-nang', 'upload/category/alisia-hotel.jpg', 86, '<p>Combo du lịch Đ&agrave; Nẵng 2020 - Vietcenter Tourist cung cấp c&aacute;c g&oacute;i Combo du lịch Đ&agrave; Nẵng với gi&aacute; tốt nhất. C&oacute; c&aacute;c combo Đ&agrave; Nẵng từ kh&aacute;ch sạn 3 sao đến kh&aacute;ch sạn 5 sao. Đa dạng lựa chọn thời gian th&iacute;ch hợp tại C&aacute;c g&oacute;i Combo Đ&agrave; Nẵng 3 ng&agrave;y 2 đ&ecirc;m. Combo Đ&agrave; Nẵng 4 ng&agrave;y 3 đ&ecirc;m. Li&ecirc;n hệ 0968.178.011 để được hỗ trợ tư vấn v&agrave; đặt combo du lịch Đ&agrave; Nẵng uy t&iacute;n v&agrave; chất lượng nhất.</p>', 2, 1, 1, '2021-03-31 05:37:49', '2021-04-27 07:13:12'),
(95, 'Combo Quy Nhơn', 'combo-quy-nhon', 'upload/category/bai-sao-phu-quoc.jpg', 86, '<p>Combo du lịch Quy Nhơn 2020 - Vietcenter Tourist cung cấp c&aacute;c g&oacute;i Combo du lịch Quy Nhơn với gi&aacute; tốt nhất. C&oacute; c&aacute;c Combo Quy Nhơn từ kh&aacute;ch sạn 3 sao đến kh&aacute;ch sạn 5 sao. Đa dạng lựa chọn thời gian th&iacute;ch hợp tại C&aacute;c g&oacute;i Combo Quy Nhơn 3 ng&agrave;y 2 đ&ecirc;m. Combo Quy Nhơn 4 ng&agrave;y 3 đ&ecirc;m. Li&ecirc;n hệ 0968.178.011 để được hỗ trợ tư vấn v&agrave; đặt Combo du lịch Quy Nhơn uy t&iacute;n v&agrave; chất lượng nhất.<p>', 2, 1, 1, '2021-03-31 05:38:03', '2021-04-17 04:47:56'),
(96, 'Combo Nha Trang', 'combo-nha-trang', 'upload/category/nha-trang.jpg', 86, '<p>Combo du lịch Nha Trang 2020 - Vietcenter Tourist cung cấp c&aacute;c g&oacute;i Combo du lịch Nha Trang với gi&aacute; tốt nhất. C&oacute; c&aacute;c Combo Nha Trang từ kh&aacute;ch sạn 3 sao đến kh&aacute;ch sạn 5 sao. Đa dạng lựa chọn thời gian th&iacute;ch hợp tại C&aacute;c g&oacute;i Combo Nha Trang 3 ng&agrave;y 2 đ&ecirc;m. Combo Nha Trang 4 ng&agrave;y 3 đ&ecirc;m. Li&ecirc;n hệ 0968.178.011 để được hỗ trợ tư vấn v&agrave; đặt Combo du lịch Nha Trang uy t&iacute;n v&agrave; chất lượng nhất.</p>', 2, 1, 1, '2021-03-31 05:38:17', '2021-04-17 04:21:49'),
(97, 'Combo Đà Lạt', 'combo-da-lat', 'upload/category/da-lat-1.jpg', 86, '<p>Combo du lịch Đ&agrave; Lạt 2020 - Vietcenter Tourist cung cấp c&aacute;c g&oacute;i Combo du lịch Đ&agrave; Lạt với gi&aacute; tốt nhất. C&oacute; c&aacute;c Combo Đ&agrave; Lạt từ kh&aacute;ch sạn 3 sao đến kh&aacute;ch sạn 5 sao. Đa dạng lựa chọn thời gian th&iacute;ch hợp tại C&aacute;c g&oacute;i Combo Đ&agrave; Lạt 3 ng&agrave;y 2 đ&ecirc;m. Combo Nha Trang 4 ng&agrave;y 3 đ&ecirc;m. Li&ecirc;n hệ 0968.178.011 để được hỗ trợ tư vấn v&agrave; đặt Combo du lịch Đ&agrave; Lạt uy t&iacute;n v&agrave; chất lượng nhất.</p>', 2, 1, 1, '2021-03-31 05:38:40', '2021-04-17 05:50:02'),
(98, 'Tour đặc biệt', 'tour-dac-biet', 'upload/category/du-lich-da-nag.png', 87, '<p>Vietcenter tổng hợp c&aacute;c chương tr&igrave;nh tour theo sự kiện, c&aacute;c dịp lễ lớn hay ng&agrave;y hội trong nước. Mời c&aacute;c bạn c&ugrave;ng t&igrave;m hiểu v&agrave; lựa chọn cho m&igrave;nh một tour du lịch th&iacute;ch hợp.</p>', 2, 1, 1, '2021-03-31 05:38:59', '2021-04-17 06:33:29'),
(99, 'Du lịch Miền Bắc', 'du-lich-mien-bac', 'upload/category/ha-giang2.jpg', 87, '<p>Du lịch Miền Bắc lu&ocirc;n l&agrave; lựa chọn tuyệt vời, hấp dẫn mọi du kh&aacute;ch từ khắp nơi tr&ecirc;n Đất Nước v&agrave; du kh&aacute;ch Quốc Tế. Với địa điểm du lịch nổi tiếng, những di t&iacute;ch lịch sử h&agrave;o h&ugrave;ng, nền văn h&oacute;a độc đ&aacute;o v&agrave; hấp dẫn.. Với lịch tr&igrave;nh d&agrave;y, khởi h&agrave;nh đ&uacute;ng thời gian cam kết, Vietcenter l&agrave; c&ocirc;ng ty lữ h&agrave;nh uy t&iacute;n nhất hiện nay tại Việt Nam, lu&ocirc;n sẵn s&agrave;ng phục vụ du kh&aacute;ch mọi l&uacute;c, mọi nơi, đảm bảo t&iacute;nh chuy&ecirc;n nghiệp v&agrave; chất lượng dịch vụ tốt nhất thị trường.</p>', 2, 1, 1, '2021-03-31 05:39:14', '2021-04-17 08:41:37'),
(100, 'Du lịch Miền Trung', 'du-lich-mien-trung', 'upload/category/bien-thuan-an-min-Copy.jpg', 87, '<p>Du lịch Miền Trung lu&ocirc;n l&agrave; lựa chọn tuyệt vời cho mọi du kh&aacute;ch. Với những khu bảo tồn thi&ecirc;n nhi&ecirc;n tuyệt vời, những th&agrave;nh phố nhộn nhịp, những di t&iacute;ch lịch sử h&agrave;o h&ugrave;ng, đường bờ biển tuyệt đẹp v&agrave; hấp dẫn, c&ugrave;ng một danh s&aacute;ch d&agrave;i những m&oacute;n ăn ngon nhất thế giới. Với lịch tr&igrave;nh d&agrave;y, khởi h&agrave;nh đ&uacute;ng thời gian cam kết, Vietcenter l&agrave; c&ocirc;ng ty lữ h&agrave;nh uy t&iacute;n nhất hiện nay tại Việt Nam, lu&ocirc;n sẵn s&agrave;ng phục vụ du kh&aacute;ch mọi l&uacute;c, mọi nơi, đảm bảo t&iacute;nh chuy&ecirc;n nghiệp v&agrave; chất lượng dịch vụ tốt nhất thị trường.</p>', 2, 1, 1, '2021-03-31 05:39:33', '2021-04-17 09:51:57'),
(101, 'Du lịch Miền Nam', 'du-lich-mien-nam', 'upload/category/sai-gon.jpg', 87, '<p>Du lịch Miền Nam lu&ocirc;n l&agrave; lựa chọn tuyệt vời cho mọi du kh&aacute;ch từ mọi miền Tổ Quốc v&agrave; Quốc Tế đổ về. Với những khu bảo tồn thi&ecirc;n nhi&ecirc;n tuyệt vời, những th&agrave;nh phố nhộn nhịp, những di t&iacute;ch lịch sử h&agrave;o h&ugrave;ng, nền văn h&oacute;a độc đ&aacute;o v&agrave; hấp dẫn, Với lịch tr&igrave;nh d&agrave;y, khởi h&agrave;nh đ&uacute;ng thời gian cam kết, Vietcenter l&agrave; c&ocirc;ng ty lữ h&agrave;nh uy t&iacute;n nhất hiện nay tại Việt Nam, lu&ocirc;n sẵn s&agrave;ng phục vụ du kh&aacute;ch mọi l&uacute;c, mọi nơi, đảm bảo t&iacute;nh chuy&ecirc;n nghiệp v&agrave; chất lượng dịch vụ tốt nhất thị trường.</p>', 2, 1, 1, '2021-03-31 05:39:53', '2021-04-17 19:34:57'),
