INSERT INTO `reviews` (`id`, `title`, `slug`, `avatar`, `image`, `name`, `address`, `position`, `category_id`, `tour_id`, `customer_id`, `summary`, `description`, `is_active`, `created_at`, `updated_at`) VALUES
(25, 'Dịch vụ chu đáo, nhiệt tình', 'dich-vu-chu-dao-nhiet-tinh', 'upload/review/tour-du-lich-cho-doanh-nghiep.png', 'upload/review/tour-du-lich-cho-doanh-nghiep.png', 'Nguyễn Hoàng', 'MZG GROUP', 1, 207, 223, 174, '<p>Ch&uacute;ng t&ocirc;i đ&atilde; lựa chọn Vietcenter Tourist tổ chức cho chuyến du lịch Singapore vừa rồi. Ch&uacute;ng t&ocirc;i thấy dịch vụ của c&aacute;c bạn rất chuy&ecirc;n nghiệp, chu đ&aacute;o. Hướng dẫn vi&ecirc;n nhiệt t&igrave;nh theo suốt đo&agrave;n. Cảm ơn Q&uacute;y C&ocirc;ng ty</p>', '<p>Ch&uacute;ng t&ocirc;i đ&atilde; lựa chọn Vietcenter Tourist tổ chức cho chuyến du lịch Singapore vừa rồi. Ch&uacute;ng t&ocirc;i thấy dịch vụ của c&aacute;c bạn rất chuy&ecirc;n nghiệp, chu đ&aacute;o. Hướng dẫn vi&ecirc;n nhiệt t&igrave;nh theo suốt đo&agrave;n. Cảm ơn Q&uacute;y C&ocirc;ng ty</p>', 1, '2021-04-20 09:22:50', '2021-05-02 21:02:15'),
(26, 'Viet Center thật tuyệt vời', 'viet-center-that-tuyet-voi', 'upload/review/avt-feedback-2.png', 'upload/review/tour-du-lich-cho-cong-ty(2).png', 'Hoàng Đức Chính', 'Hùng Thắng Company', 1, 207, 160, 226, '<p>Dịch vụ chu đ&aacute;o v&agrave; chuy&ecirc;n nghiệp. Ch&uacute;ng t&ocirc;i ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m v&agrave; tin tưởng sử dụng dịch vụ của Vietcenter cho những chuyến đi du lịch lần sau. Xin cảm ơn Q&uacute;y c&ocirc;ng ty.</p>', '<p>Năm nay, lễ hội&nbsp;<a href=\"https://dulich.vnexpress.net/photo/anh-video/hoa-tam-giac-mach-dang-dua-no-tren-cao-nguyen-da-3829864.html\">hoa tam gi&aacute;c mạch</a>&nbsp;khai mạc ng&agrave;y 10/11. Đường hoa tại phố cổ Đồng Văn k&eacute;o d&agrave;i tới th&aacute;ng 12 l&agrave; điểm mới của lễ hội năm nay. Ngo&agrave;i ra, du kh&aacute;ch c&oacute; thể tham gia triển l&atilde;m ảnh C&ocirc;ng vi&ecirc;n địa chất; hội thi hoa tam gi&aacute;c mạch; hội thi b&ograve; đẹp huyện Đồng Văn tại x&atilde; S&agrave; Ph&igrave;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://vietcenter.vn/Uploads/images/tour/tou9.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>X&atilde; Vần Chải, Lũng Thầu, Sủng L&agrave;, Phố C&aacute;o, Phố L&agrave;, ng&atilde; ba Lũng T&aacute;o - Ma L&eacute; - Lũng C&uacute;, thị trấn Đồng Văn l&agrave; những điểm ngắm hoa tam gi&aacute;c mạch được nhiều du kh&aacute;ch t&igrave;m đến. UBND huyện Đồng Văn đ&atilde; quy hoạch lại c&aacute;c v&ugrave;ng trồng hoa, diện t&iacute;ch mỗi điểm rộng từ 1 tới 3 ha.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://vietcenter.vn/Uploads/images/tour/tour10.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ở một số điểm c&ograve;n c&oacute; chương tr&igrave;nh biểu diễn văn nghệ v&agrave; tr&ograve; chơi d&acirc;n gian với sự hướng dẫn của hoạt n&aacute;o vi&ecirc;n.</p>\r\n\r\n<p>Lễ hội hoa tam gi&aacute;c mạch lần đầu được tổ chức v&agrave;o năm 2015. Sự kiện diễn ra h&agrave;ng năm nhằm quảng b&aacute; c&aacute;c gi&aacute; trị di sản văn h&oacute;a, di t&iacute;ch lịch sử, danh lam thắng cảnh của H&agrave; Giang, đồng thời k&ecirc;u gọi đầu tư, th&uacute;c đẩy du lịch ph&aacute;t triển.</p>\r\n\r\n<p>X&atilde; Vần Chải, Lũng Thầu, Sủng L&agrave;, Phố C&aacute;o, Phố L&agrave;, ng&atilde; ba Lũng T&aacute;o - Ma L&eacute; - Lũng C&uacute;, thị trấn Đồng Văn l&agrave; những điểm ngắm hoa tam gi&aacute;c mạch được nhiều du kh&aacute;ch t&igrave;m đến. UBND huyện Đồng Văn đ&atilde; quy hoạch lại c&aacute;c v&ugrave;ng trồng hoa, diện t&iacute;ch mỗi điểm rộng từ 1 tới 3 ha.</p>\r\n\r\n<p>Ở một số điểm c&ograve;n c&oacute; chương tr&igrave;nh biểu diễn văn nghệ v&agrave; tr&ograve; chơi d&acirc;n gian với sự hướng dẫn của hoạt n&aacute;o vi&ecirc;n.</p>\r\n\r\n<p>Lễ hội hoa tam gi&aacute;c mạch lần đầu được tổ chức v&agrave;o năm 2015. Sự kiện diễn ra h&agrave;ng năm nhằm quảng b&aacute; c&aacute;c gi&aacute; trị di sản văn h&oacute;a, di t&iacute;ch lịch sử, danh lam thắng cảnh của H&agrave; Giang, đồng thời k&ecirc;u gọi đầu tư, th&uacute;c đẩy du lịch ph&aacute;t triển.</p>\r\n\r\n<p>X&atilde; Vần Chải, Lũng Thầu, Sủng L&agrave;, Phố C&aacute;o, Phố L&agrave;, ng&atilde; ba Lũng T&aacute;o - Ma L&eacute; - Lũng C&uacute;, thị trấn Đồng Văn l&agrave; những điểm ngắm hoa tam gi&aacute;c mạch được nhiều du kh&aacute;ch t&igrave;m đến. UBND huyện Đồng Văn đ&atilde; quy hoạch lại c&aacute;c v&ugrave;ng trồng hoa, diện t&iacute;ch mỗi điểm rộng từ 1 tới 3 ha.</p>\r\n\r\n<p>Ở một số điểm c&ograve;n c&oacute; chương tr&igrave;nh biểu diễn văn nghệ v&agrave; tr&ograve; chơi d&acirc;n gian với sự hướng dẫn của hoạt n&aacute;o vi&ecirc;n.</p>\r\n\r\n<p>Lễ hội hoa tam gi&aacute;c mạch lần đầu được tổ chức v&agrave;o năm 2015. Sự kiện diễn ra h&agrave;ng năm nhằm quảng b&aacute; c&aacute;c gi&aacute; trị di sản văn h&oacute;a, di t&iacute;ch lịch sử, danh lam thắng cảnh của H&agrave; Giang, đồng thời k&ecirc;u gọi đầu tư, th&uacute;c đẩy du lịch ph&aacute;t triển.</p>', 1, '2021-04-20 09:26:00', '2021-05-02 21:02:04'),
(27, 'Hỗ trợ nhiệt tình, chu đáo', 'ho-tro-nhiet-tinh-chu-dao', 'upload/review/avt-feedback-3.png', 'upload/review/tour-du-lich-cho-doanh-cong-ty-doanh-nghiep-vietcenter3.png', 'Nguyễn Như Xuân', 'ADC Việt Nam', 1, 207, 237, 210, '<p>Năm nay, lễ hội hoa tam gi&aacute;c mạch khai mạc ng&agrave;y 10/11. Đường hoa tại phố cổ Đồng Văn k&eacute;o d&agrave;i tới th&aacute;ng 12 l&agrave; điểm mới của lễ hội năm nay.</p>', '<p>Năm nay, lễ hội hoa tam gi&aacute;c mạch khai mạc ng&agrave;y 10/11. Đường hoa tại phố cổ Đồng Văn k&eacute;o d&agrave;i tới th&aacute;ng 12 l&agrave; điểm mới của lễ hội năm nay.</p>', 1, '2021-04-20 09:27:21', '2021-05-02 21:01:50'),
(28, 'Mùa hoa Tam giác mạch', 'mua-hoa-tam-giac-mach', 'upload/review/cach-trai-ga-giuong-khach-san-0.jpg', 'upload/review/dao-diep-son-nha-trang.jpg', 'Nguyễn Xuân Trường', 'FPT Telecom', 1, 207, 254, 230, '<p>Lễ hội hoa tam gi&aacute;c mạch lần đầu được tổ chức v&agrave;o năm 2015. Sự kiện diễn ra h&agrave;ng năm nhằm quảng b&aacute; c&aacute;c gi&aacute; trị di sản văn h&oacute;a, di t&iacute;ch lịch sử, danh lam thắng cảnh của H&agrave; Giang, đồng thời k&ecirc;u gọi đầu tư, th&uacute;c đẩy du lịch ph&aacute;t triển.</p>', '<p>Lễ hội hoa tam gi&aacute;c mạch lần đầu được tổ chức v&agrave;o năm 2015. Sự kiện diễn ra h&agrave;ng năm nhằm quảng b&aacute; c&aacute;c gi&aacute; trị di sản văn h&oacute;a, di t&iacute;ch lịch sử, danh lam thắng cảnh của H&agrave; Giang, đồng thời k&ecirc;u gọi đầu tư, th&uacute;c đẩy du lịch ph&aacute;t triển.</p>\r\n\r\n<p>X&atilde; Vần Chải, Lũng Thầu, Sủng L&agrave;, Phố C&aacute;o, Phố L&agrave;, ng&atilde; ba Lũng T&aacute;o - Ma L&eacute; - Lũng C&uacute;, thị trấn Đồng Văn l&agrave; những điểm ngắm hoa tam gi&aacute;c mạch được nhiều du kh&aacute;ch t&igrave;m đến. UBND huyện Đồng Văn đ&atilde; quy hoạch lại c&aacute;c v&ugrave;ng trồng hoa, diện t&iacute;ch mỗi điểm rộng từ 1 tới 3 ha.</p>', 1, '2021-04-25 03:31:59', '2021-05-02 21:01:37'),
(29, 'Du lịch bằng tàu thủy', 'du-lich-bang-tau-thuy', 'upload/review/trai-nghiem-mua-dong-2018-tuyet-voi-o-nhat-ban-chi-tu-980000-vnđ-11-6-2018-1.jpg', 'upload/review/cát-bà-3-min.png', 'Phạm Huy Hoàng', 'Viettel Telecom', 1, 207, 230, 228, '<p>Nếu việc du lịch bằng m&aacute;y bay hay t&agrave;u hỏa đ&atilde; trở n&ecirc;n qu&aacute; phổ biến th&igrave; việc kh&aacute;m ph&aacute; c&aacute;c địa điểm mới lạ bằng t&agrave;u biển liệu c&oacute; l&agrave;m bạn cảm thấy th&uacute; vị? C&ugrave;ng t&igrave;m hiểu xem du lịch quốc tế bằng t&agrave;u biển c&oacute; g&igrave; mới mẻ hay&nbsp; kh&ocirc;ng nh&eacute;?</p>', '<h2><strong>Nếu việc du lịch bằng m&aacute;y bay hay t&agrave;u hỏa đ&atilde; trở n&ecirc;n qu&aacute; phổ biến th&igrave; việc kh&aacute;m ph&aacute; c&aacute;c địa điểm mới lạ bằng t&agrave;u biển liệu c&oacute; l&agrave;m bạn cảm thấy th&uacute; vị? C&ugrave;ng t&igrave;m hiểu xem du lịch quốc tế bằng t&agrave;u biển c&oacute; g&igrave; mới mẻ hay&nbsp; kh&ocirc;ng nh&eacute;?</strong></h2>\r\n\r\n<h3><strong>Đi du lịch bằng du thuyền 5 sao - một trải nghiệm kh&ocirc;ng hề nh&agrave;m ch&aacute;n</strong></h3>', 1, '2021-04-25 03:33:18', '2021-05-02 21:01:24'),
(30, 'Học sinh sinh viên', 'hoc-sinh-sinh-vien', 'upload/review/20170723-014645-kinh-nghiem-du-lich-hoi-an-mytour-2_600x400.jpg', 'upload/review/giay-to-can-chuan-bi-cho-tre-em-khi-di-may-bay.jpg', 'Trương Tấn Tài', 'VNPT Telecom', 1, 207, 179, 195, '<p>C&aacute;c sự kiện cho sinh vi&ecirc;n ng&agrave;y c&agrave;ng nở rộ khiến cho việc lựa chọn&nbsp;địa điểm tổ chức sự kiện cho học sinh sinh vi&ecirc;n&nbsp;được quan t&acirc;m nhiều hơn. Đặc trưng của c&aacute;c bạn&nbsp;l&agrave; đa dạng, trẻ trung, năng động, ham học hỏi v&agrave; lu&ocirc;n hứng th&uacute; với những điều mới mẻ. Với đặc th&ugrave; năng động v&agrave; hoạt b&aacute;t của những người trẻ, c&aacute;c sự kiện cho sinh vi&ecirc;n n&ecirc;n được tiến h&agrave;nh ở những nơi tho&aacute;ng m&aacute;t, rộng r&atilde;i, kh&ocirc;ng kh&iacute; trong l&agrave;nh. Đ&acirc;y l&agrave; điều kiện thuận lợi để sinh vi&ecirc;n c&oacute; thể ph&aacute;t huy sức s&aacute;ng tạo của m&igrave;nh. Trong b&agrave;i viết n&agrave;y, Vietcenter Tourist xin gợi &yacute; một địa điểm tổ chức sự kiện rất ph&ugrave; hợp với c&aacute;c bạn học sinh, sinh vi&ecirc;n. Địa điểm n&agrave;y ho&agrave;n to&agrave;n mới v&agrave; c&oacute; vị tr&iacute; rất HOT tại H&agrave; Nội.</p>', '<p>C&aacute;c sự kiện cho sinh vi&ecirc;n ng&agrave;y c&agrave;ng nở rộ khiến cho việc lựa chọn<strong>&nbsp;địa điểm tổ chức sự kiện cho học sinh sinh vi&ecirc;n</strong>&nbsp;được quan t&acirc;m nhiều hơn. Đặc trưng của c&aacute;c bạn&nbsp;l&agrave; đa dạng, trẻ trung, năng động, ham học hỏi v&agrave; lu&ocirc;n hứng th&uacute; với những điều mới mẻ. Với đặc th&ugrave; năng động v&agrave; hoạt b&aacute;t của những người trẻ, c&aacute;c sự kiện cho sinh vi&ecirc;n n&ecirc;n được tiến h&agrave;nh ở những nơi tho&aacute;ng m&aacute;t, rộng r&atilde;i, kh&ocirc;ng kh&iacute; trong l&agrave;nh. Đ&acirc;y l&agrave; điều kiện thuận lợi để sinh vi&ecirc;n c&oacute; thể ph&aacute;t huy sức s&aacute;ng tạo của m&igrave;nh. Trong b&agrave;i viết n&agrave;y, Vietcenter Tourist xin gợi &yacute; một địa điểm tổ chức sự kiện rất ph&ugrave; hợp với c&aacute;c bạn học sinh, sinh vi&ecirc;n. Địa điểm n&agrave;y ho&agrave;n to&agrave;n mới v&agrave; c&oacute; vị tr&iacute; rất HOT tại H&agrave; Nội.</p>', 1, '2021-04-25 03:34:38', '2021-05-02 21:01:09');
